// Get SSH output (installed version)
const sshOutput = $node["Get Installed Version"].json.stdout;
const installedVersion = sshOutput.trim();

// Get GitHub release data
const githubData = $node["Get Latest GitHub Release"].json;
const latestTag = githubData.tag_name; // e.g. "n8n@1.75.2" or "1.75.2"
const latestVersion = latestTag.replace(/^n8n@/, '').replace(/^v/, '');
const releaseUrl = githubData.html_url;
const releaseName = githubData.name || latestTag;
const publishedAt = githubData.published_at;

// Parse version strings into comparable numbers
function parseVersion(v) {
  const clean = v.replace(/^n8n@/, '').replace(/^v/, '').trim();
  const parts = clean.split('.').map(Number);
  return { major: parts[0] || 0, minor: parts[1] || 0, patch: parts[2] || 0, raw: clean };
}

const installed = parseVersion(installedVersion);
const latest = parseVersion(latestVersion);

// Compare versions
let needsUpgrade = false;
if (latest.major > installed.major) {
  needsUpgrade = true;
} else if (latest.major === installed.major && latest.minor > installed.minor) {
  needsUpgrade = true;
} else if (latest.major === installed.major && latest.minor === installed.minor && latest.patch > installed.patch) {
  needsUpgrade = true;
}

return [{
  json: {
    installedVersion: installed.raw,
    latestVersion: latest.raw,
    needsUpgrade,
    releaseUrl,
    releaseName,
    publishedAt
  }
}];
